for (int i = 0 ; i < 10 ; i++) { // Good.
    prices[i] = 0;
}

SUBSECTION.BAD_CODE_SNIPPET:
for (auto i = &prices[0]; i < &prices[10]; ++i) { // Bad
    *i = 0;                        // i is not of integer type
}

