BODY.BAD_CODE_SNIPPET:
class MyClass {
public:
    double energy() const {return mEnergy;}
    double momentum() const {
        double p2 = mEnergy*mEnergy - mMass2;
        return sqrt(p2;)
    }
 };

BODY.CODE_SNIPPET:
class MyClass {
public:
    double energy() const;
    double momentum() const;
};

inline double MyClass::energy() const {return mEnergy;}
inline double MyClass::momentum() const {
    double p2 = mEnergy*mEnergy - mMass2;
    return sqrt(p2;)
}

int value = *valuePointer;
int* valuePointer = &value;
int value = myObject->getValue();
int value = myStruct.value;

// Good.

// Pointers
char* myCharacter;  // typical C++ notation
char *myCharacter;  // Kerningham & Ritchie's  notation

// References
const string& myString;
const string &myString;

if (thisOneThing > thisOtherThing &&
    aThirdThing == aFourthThing &&
    yetAnother && lastOne) {
    ...
}

int x = 3;                           // preferred style (must using auto)
std::string name = "Some Name";

int x { 3 };                         // also possible
std::string name{ "Some Name" };
std::string name = { "Some Name" };

// Good - directives at beginning of line
if (lopsidedScore) {
#if DISASTER_PENDING
dropEverything();
# if NOTIFY               // Good but not required -- Spaces after #
notifyClient();
# endif
#endif
goBackToNormal();
}

BODY.BAD_CODE_SNIPPET:
// Bad - indented directives
if (lopsidedScore) {
#if DISASTER_PENDING  // The "#if" should be at beginning of line
    dropEverything();
#endif                // Do not indent "#endif"
    goBackToNormal();
}

